pragma solidity ^0.4.18;
contract BitBrawlActionControl {
	address owner;
	address bteam = 0xe6f98D9271A1ABe4764cb0Bd0b9B60338cE18f8E;

	function BitBrawlBase() public { owner = msg.sender; }
	function destroy() public onlyOwner { selfdestruct(owner); }
	function transferOwnership(address newOwner) public onlyOwner {
		if (newOwner != address(0)) {
			owner = newOwner;
		}
	}

	modifier onlyOwner() {
		require (msg.sender == owner);
		_;
	}

	modifier onlyBTeam() {
		require (msg.sender == bteam);
		_;
	}
}

// The core of the BitBrawl contract
contract BitBrawl is BitBrawlActionControl {
	enum Type { WATER, FIRE, EARTH, AIR }

	mapping (address => Player) players;

	event TradeEvent(address to, address from, uint idTo, uint idFrom);
	event CreateEvent(address owner, uint64 id);

	// Our main Card array, stores all cards ever created
	Card[] cards;
	uint64 internal curCardNum;

	struct Player {
		uint64[] cards; // a list of cards by ID
	}

	struct Card {
		string name; // name of the card, might randomize this at some point to give it SPICE
		uint64 creationTime; // timestamp
		uint64 id; // index of this card, with the first card created having id=0
		uint16 imageId; // imageId of the card
		uint16 atk; // attack
		uint16 fort; // fortitude
		uint16 element;
		address owner;
	}

	function getPlayerCards(address _player) public view returns (uint64[]) {
		Player storage player = players[_player];

		return player.cards;
	}

	function createCard(address _owner, string _name, uint _imageId) external returns (uint64) {
		require (owner != address(0));

		// Create our card struct
		Card memory card = Card({
			name: _name,
			creationTime: uint64(now),
			id: curCardNum,
			imageId: 0, //TODO: Change this jaunt to different images
			// BIG TODO: Making random numbers like this is insecure!! Use commit-and-reveal generation instead (https://www.reddit.com/r/ethereum/comments/75lrxs/how_do_smart_contracts_generate_random_numbers/)
			atk: uint16(block.blockhash(block.number - 1)) % 15 + 1,
			fort: uint16(block.blockhash(block.number - 1)) % 15 + 1,
			element: uint16(block.blockhash(block.number - 1)) % 4 + 1,
			owner: _owner
		});

		curCardNum++;

		uint64 newCardId = uint64(cards.push(card) - 1);

		// Trigger all the necessary events
		CreateEvent(_owner, newCardId);

		return newCardId;
	}
}
